mapper.select:
select
    wu.USER_ID ,
    wu.USER_NAME ,
    wu.EMP_ID ,
    wu.EMP_NAME
from papla_wf_run_ins_task t, papla_wf_user_info wu
where t.ASSIGN_USER = xp.USER_ID
and t.INSTANCE_id = #{instanceId}
and t.ACT_ID = #{id}
and t.TASK_EFFECTIVE = 'Y'
and t.create_type != 'consult'
and t.create_type != 'copyto'
and t.close_cause != 'countersign'
and t.BEGIN_DATE =
    (select min(c.BEGIN_DATE)
        from (
            select d.BEGIN_DATE from papla_wf_run_ins_task d
            where d.ACT_ID = #{id}
            and d.INSTANCE_ID = #{instanceId}
            and d.task_effective = 'Y'
            ) c
    )

select t.queue_id, t.instance_code, t.act_code, t.orderby, t.node_name, t.task_title, t.approver, t.approve_status, t.create_dt, t.create_by, t.update_by, t.update_dt from papla_wf_run_ins_act_queue t where t.orderby = (select min(a.orderby) from papla_wf_run_ins_act_queue a where a.instance_code = #{insCode} and a.act_code = #{actCode}
and a.queue_id not in
#{item}
and a.approve_status = 'open') and t.act_code = #{actCode} and t.instance_code = #{insCode} and t.approve_status = 'open'
